import React from 'react';
import Layout from '../components/MyLayout';
import LikeButton from '../components/LikeButton';
import Select from 'react-select';
import Table from 'react-bootstrap/Table';
import Container from 'react-bootstrap/Container';
import Row from 'react-bootstrap/Row';
import Col from 'react-bootstrap/Col';

import CustomerJsonData from '../customers-file-map.json';
import DocumentJsonData from '../documents-file-map.json';

const customers = [];
Object.keys(CustomerJsonData).map((key, i) => (
    customers.push({key})
))

export default function Classification() {
    return(
        <Layout>
            <title>Document Classifications by Customer</title>
            <Container>
                <Row>
                    <Col>Document Classifications by Customer</Col>
                </Row>
                <Row>
                    <Col>
                        {/* <Dropdown>
                            <Dropdown.Toggle variant="sucecss" id="customer-names-dropdown">
                                Customer Names
                            </Dropdown.Toggle>

                            <Dropdown.Menu> */}


                            {/* <Multiselect id="customer-names-dropdown" multiple="multiple"> */}

                            {
                                    <Select options={customers} isMulti='true' />
                            }

                            {/* </Multiselect> */}
                                {/* <Dropdown.Item as="button">First Customer</Dropdown.Item>
                                <Dropdown.Item as="button">Second Customer</Dropdown.Item> */}
                                {/* <Dropdown.Divider />
                                <Dropdown.Item as="button">Clear Selected Customers</Dropdown.Item>
                            </Dropdown.Menu>
                        </Dropdown> */}
                    </Col>
                    <Col>




                        <select id="document-types-dropdown" multiple="multiple">

                        {
                            Object.keys(DocumentJsonData).map((key, i) => (
                            <option value={key}>{key}</option>
                            ))
                        }

                        </select>





                    </Col>
                </Row>


                <div>
                    <Table striped bordered hover>
                        <thead>
                            <tr>
                                <th>Document Types</th>
                                <th>First Customer</th>
                                <th>Second Customer</th>
                            </tr>
                        </thead>
                        <tbody>
                            <tr>
                                <td>Document A</td>
                                <td>First Customer's Document A</td>
                                <td>Second Customer's Document A</td>
                            </tr>
                            <tr>
                                <td>Document B</td>
                                <td>First Customer's Document B</td>
                                <td>Second Customer's Document B</td>
                            </tr>
                        </tbody>
                    </Table>
                </div>

                <div>
                    <p><LikeButton></LikeButton></p>
                </div>
            </Container>
        </Layout>
    );
};
